    border-radius: 25px;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
}

.mouse_icon .mouse_scrollwheel {
    display: block;
    width: 8px;
    height: 15px;
    background-color: var(--primary-color);
    margin: 0 auto;
    margin-top: 5px;
    border-radius: 25px;
    transition: ease .2s;
    animation: mouse 1.6s infinite;
}

.hero_hashtag {
    margin-left: auto;
    position: relative; 
    writing-mode: vertical-rl;
    text-orientation: mixed;
    margin-bottom: -50px;
}

.hero_hashtag::after {
    content: '';
    display: inline-block;
    height: 50px;
    width: 3px;
    background-color: var(--primary-color);
    margin: 0;
    margin-top: 15px;
}

/* Countdown */

.countdown {
    padding: 100px 155px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: var(--accent-color);
}

.date {
    margin-bottom: 30px;
    text-transform: uppercase;
    text-align: center;
}

.time-left {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}

.time-left h2 {
    font-size: var(--countdown);
    font-weight: 400;
    padding-bottom: 15px;
}

.time-left h2:not(:last-child) {
    margin: 0 30px;
}

/* Wedding info */

.wedding_info {
    padding: var(--padding-regular-section);
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
}

.wedding_info h3 {
    margin-bottom: 100px;
}

.wedding_times {
    display: flex;
    margin-bottom: 100px;
}

.wedding_time {
    margin: 0 120px;
}

.wedding_time h4 {
    margin-bottom: 10px;
}

.wedding_location {
    margin-bottom: 50px;
}

.wedding_infoButton {
    margin-bottom: 100px;
    color: #000;
}


/* Animation Keyframes */


@keyframes mouse {
    0% {
        height: 0;
        opacity: 0;
    }

    30% {
        height: 17px;
        opacity: 1;
    }

    100% {
        transform: translateY(15px);
        opacity: 0;
    }

}

/* Media queries */

@media screen and (max-width: 1440px) {
    
    .hero_bottom {
        padding: 0 40px;
        padding-bottom: 40px;
    }

    .hero_hashtag {
        margin-bottom: -40px;
    }

    .countdown {
        padding: 95px;
    }

}

@media screen and (max-width: 1250px) {
    .menu_text {
        color: var(--text-color);
    }

    .menu_hamburguer {
        display: block;
    }
    
    .menu_hamburguer span {
        transform-origin: 10px 1px;
        transition: ease .6s;
    }
    
    .close span:first-child{
        transform: rotate(45deg);
    }
    .close span:last-child{
        transform: rotate(-45deg);
    }

    .menu_button {
        color: var(--text-color);
        font-size: var(--text);
        transform: translateY(100px);
        opacity: 0;
        transition: none;
    }

    .nav {
        position: fixed;
        top: 0;
        left: 0;
        height: 100vh;
        flex-direction: column;
        background-color: var(--secondary-color);
        justify-content: center;
        z-index: 2;
        clip-path: circle(0.3% at 100% 0);
    }

    .menu_text {
        transform: translateY(100px);
        opacity: 0;
    }

    .main_menu {
        flex-direction: column;
        height: 100%;
        margin-bottom: 30px;
        
    }


}

@media screen and (max-width: 1100px) {

    .main_menu {
        margin-bottom: 75px;
    }

    .menu_button {
        font-size: var(--button)
    }

    .menu_hamburguer {
        position: fixed;
        top: 55px;
        right: 45px;
    }

    .hero {
        height: 100%;
    }

    .hero_main {
        padding-top: 95px;
        margin: auto 0;
    }

    .countdown {
        padding: 80px 45px;
    }

    .wedding_time {
        margin: 0 70px;
    }

}

@media screen and (max-width: 680px) {

    .hero_savetd {
        display: none;
    }

    .nav {
        padding: 0 20px;
    }

    .main_menu {
        margin-bottom: 25px;
    }

    .menu_hamburguer {
        position: fixed;
        top: 55px;
        right: 25px;
    }

    .menu_hamburguer span {
        width: 30px;
        height: 3px;
    }

    .countdown {
        padding: 30px 60px;
    }

    .wedding_info h3 {
        margin-bottom: 75px;
    }

    .wedding_times {
        display: flex;
        flex-direction: column;
        margin-bottom: 75px;
    }

    .wedding_time {
        margin: 25px 0;
    }

}

@media screen and (max-width: 378px) {

    .hero_hashtag::after {
        height: 35px;
    }

    .hero_main {
        padding-top: 115px;
        margin: auto 0;
    }
    .hero_bottom .mouse_icon {
        width: 24px;
        height: 40px;
        margin-top: -6px;
    }
    
    .mouse_icon .mouse_scrollwheel {
        width: 8px;
        height: 10px;
        margin-top: 5px;

    }

    .countdown {
        padding: 55px 20px;
    }

}
