/* Global classes */

.menu_text {
    font-family: var(--roaster-light);
    font-size: var(--menu-text);
    color: var(--secondary-color);
    text-decoration: none;
    margin: 0 20px;
    padding: 5px 0;
}

.menu_text::after {
    content: '';
    display: block;
    width: 0%;
    height: 3px;
    background-color: var(--primary-color);
    transition: ease-in-out .5s;
}

.menu_text:hover::after {
    width: 100%;
}

.main_heading {
    font-family: var(--great-vibes);
    font-size: var(--main-heading);
    color: var(--secondary-color);
    font-weight: 400;
}

.main_heading span{
    color: var(--accent-color);
}

.heading {
    font-family: var(--roaster-light);
    font-size: var(--heading);
    font-weight: 300;
}

.sub_heading {
    font-family: var(--roaster-regular);
    font-size: var(--sub-heading);
    font-weight: 400;
    color: var(--primary-color);
}

.sub_heading2 {
    font-family: var(--roaster-light);
    font-size: var(--sub-heading2);
    font-weight: 300;
}

.sub_heading3 {
    font-family: var(--roaster-light);
    font-size: var(--sub-heading3);
    letter-spacing: 5px;
    font-weight: 300;
    text-transform: uppercase;
}

.text {
    font-family: var(--roaster-light);
    font-size: var(--text);
    letter-spacing: 135.7%;
}

.hashtag {
    font-family: var(--roaster-light);
    font-size: var(--hashtag);
    letter-spacing: 103.2%;
    color: var(--secondary-color);
    font-weight: 400;
}

.button {
    width: 345px;
    height: 65px;
    border: 2px solid var(--primary-color);
    background-color: transparent;
    font-family: var(--roaster-light);
    font-size: var(--button);
    color: var(--secondary-color);
    text-transform: uppercase;
    cursor: pointer;
    transition: ease .4s;
    opacity: 0.8;
}

.button:hover {
    opacity: 1;
    transform: scale(1.02);
}

.map_button {
    position: relative;
    background-color: transparent;
    border: none;
    font-family: var(--roaster-light);
    font-size: var(--map-button);
    cursor: pointer;
    transition: ease .4s;
    width: fit-content;
    padding: 0;
}

.map_button::after {
    content: '';
    display: block;
    height: 3px;
    width: 100%;
    background-color: #1F1F1F;
    margin-top: 5px;
    transition: ease .4s;
}

.map_button:hover::after {
    width: 0;
    margin-left: auto;
}

.map_button:hover {
    transform: scale(1.04);
}

.time {
    font-family: var(--roaster-light);
    font-size: var(--time);
}

@media screen and (max-width: 1250px) {

    .menu_text {
        margin: 10px 0;
    }

    .button {
        width: 415px;
        height: 75px;
    }
}

@media screen and (max-width: 1100px) {

    .menu_text {
        margin: 10px 0;
    }

    .button {
        width: 595px;
        height: 115px;
    }
