    .button {
        width: 280px;
        height: 65px;
    }

}

/* Nav/Hero */

/* Navbar */

.menu_hamburguer {
    position: fixed;
    top: 45px;
    right: 35px;
    display: none;
    z-index: 5;
}

.menu_hamburguer span {
    display: block;
    width: 35px;
    height: 4px;
    background-color: var(--primary-color);
    border-radius: 25px;
}

.menu_hamburguer span:not(:last-child) {
    margin-bottom: 4px;
}

.nav {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 95px;
}

.main_menu {
    height: 145px;
    display: flex;
    align-items: center; 
    margin-right: auto;
}


/* Header */

.header {
    background-image: url(/images/hero_bg.jpg);
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    height: 100vh;
    max-height: 100vh;
    width: 100%;
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.hero {
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100%;
}

.hero_main {
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100%;
    margin: auto 0;
}

.hero_main h2 {
    color: var(--secondary-color);
}

.hero_bottom {
    position: relative;
    width: 100%;
    padding: 50px 95px;
    display: flex;
    align-items: flex-end;
}

.hero_bottom .mouse_icon {
    width: 29px;
    height: 48px;
    margin-top: -6px;
    border: 2px solid var(--primary-color);
    border-radius: 25px;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
}

.mouse_icon .mouse_scrollwheel {
    display: block;
    width: 8px;
    height: 15px;
    background-color: var(--primary-color);
    margin: 0 auto;
    margin-top: 5px;
    border-radius: 25px;
    transition: ease .2s;
    animation: mouse 1.6s infinite;
}

.hero_hashtag {
    margin-left: auto;
    position: relative; 
    writing-mode: vertical-rl;
    text-orientation: mixed;
    margin-bottom: -50px;
}

.hero_hashtag::after {
    content: '';
    display: inline-block;
    height: 50px;
    width: 3px;
    background-color: var(--primary-color);
    margin: 0;
    margin-top: 15px;
}

/* Countdown */

.countdown {
    padding: 100px 155px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: var(--accent-color);
}

.date {
    margin-bottom: 30px;
    text-transform: uppercase;
    text-align: center;
}

.time-left {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}

.time-left h2 {
    font-size: var(--countdown);
    font-weight: 400;
    padding-bottom: 15px;
}

.time-left h2:not(:last-child) {
    margin: 0 30px;
}

/* Wedding info */

.wedding_info {
    padding: var(--padding-regular-section);
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
}

.wedding_info h2 {
    margin-bottom: 100px;
}

.wedding_times {
    display: flex;
    margin-bottom: 100px;
}

.wedding_time {
    margin: 0 120px;
}

.wedding_time h4 {
    margin-bottom: 10px;
}

.wedding_location {
    margin-bottom: 50px;
}

.wedding_infoButton {
    color: #000;
}

.wedding_mapButton {
    margin-bottom: 100px;
}

/* Our story */

.story {
    display: flex;
}

.story_imgContainer {
    overflow: hidden;   
    width: 45%;
    display: flex;
    justify-content: center;
    align-items: center;
}

.story_img {
    height: 100vh;
    object-fit: cover;
    object-position: center;
}

.story_content {
    width: 55%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: var(--padding-image-section);
}

.story_content h4 {
    margin-bottom: 10px;
    color: var(--primary-color);
}

.story_content h2 {
    margin-bottom: 75px;
}

.story_content p:not(:last-child) {
    margin-bottom: 30px;
}

/* Location */

.location {
    padding: var(--padding-regular-section);
    text-align: center;
}

.location h2 {
    margin-bottom: 150px;
}

.location_sub {
    color: var(--primary-color);
}

.location_imgContainer {
    margin-bottom: 150px;
    height: 60vh;
    display: flex;
    overflow: hidden;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

.location_img {
    object-position: center;
}

.location_content h4 {
    margin-bottom: 30px;
}

.location_content p {
    margin-bottom: 80px;
}

.location_links {
    display: flex;
    justify-content: center;
}

.location_links button{
    margin: 0 80px;
}

/* Gallery */

.gallery {
    padding: var(--padding-regular-section);
    background-color: var(--accent-color);
}

.gallery h4 {
    margin-bottom: 10px;
}

.gallery h2 {
    margin-bottom: 30px;
}

.gallery p {
    width: 40vw;
}

.gallery_imgContainer {
    width: 70vw;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: 100px 200px;
    gap: 10px;
    margin-left: auto;
    margin-top: 100px;
}

.gallery_item {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    cursor: pointer;
}

.gallery_img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: ease .8s;
}

.gallery_item:hover .gallery_img {
    transform: scale(1.08);
}

.gallery_imgText {
    background-color: rgba(0, 0, 0, 0.658);
    position: absolute;
    padding: 20px;
    bottom: 20px;
    right: 20px;
    color: var(--secondary-color);
    opacity: 0;
    transition: ease .6s;
}

.gallery_imgText h5 {
    margin-bottom: 10px;
}

.gallery_item:hover .gallery_imgText{
    opacity: 1;
}

.h-2 {
    grid-row: span 2;
}
.h-3 {
    grid-row: span 3;
}

.gallery_popup {
    top: 0;
    left: 0;
    position: fixed;
    height: 100vh;
    width: 100%;
    background-color: rgba(0, 0, 0, 0.572);
    z-index: 6;
    justify-content: center;
    align-items: center;
    padding: 100px 200px;
    display: none;
}

.popup_img {
    width: 100%;
    height: 100%;
    object-fit: contain;
    display: none;   
}

.popup_close {
    position: absolute;
    right: 80px;
    top: 60px;
    font-size: 35px;
    color: var(--primary-color);
    cursor: pointer;
    animation: close 1.5s infinite;
}

.gallery_popup--active {
    display: flex;
    animation: opacity .8s ease-in-out;
    opacity: 1;
}

.popup_img--active {
    display: block;
}

/* Events */

.events {
    padding: var(--padding-regular-section);
    text-align: center;
}

.events h4 {
    margin-bottom: 30px;
}

.timeline {
    position: relative;
    margin: 75px auto;
    width: 100%;
}

.timeline::after {
    content: '';
    position: absolute;
    width: 3px;
    background-color: var(--accent-color);
    top: 0;
    bottom: 0;
    left: 50%;
    transform: translateX(-3px);
}

.container {
    padding: 50px 0;
    position: relative;
    width: 50%;
}

.container::after {
    content: '';
    position: absolute;
    width: 30px;
    height: 30px;
    background-color: var(--primary-color);
    border-radius: 100%;
}

.content {
    position: relative;
    padding: 50px 150px;
}

.left {
    left: 0%;
}

.left::after {
    right: 0;
    top: 50%;
    transform: translateX(50%);
    z-index: 2;
}

.right {
    left: 50%;
}

.right::after {
    left: 0;
    top: 50%;
    transform: translateX(-50%);
    z-index: 2;
}

.content {
    text-align: left;
}

.content h3 {
    margin-bottom: 20px;
    font-family: var(--roaster-regular);
}

.content_time {
    color: var(--primary-color);
}

.content p {
    margin-top: 10px;
}

/* Animation Keyframes */


@keyframes mouse {
    0% {
        height: 0;
        opacity: 0;
    }

    30% {
        height: 17px;
        opacity: 1;
    }

    100% {
        transform: translateY(15px);
        opacity: 1  ;
    }

}

@keyframes close {
    0% {
        transform: scale(1);
    }
    
    50% {
        transform: scale(1.2);
    }

    100% {
        transform: scale(1);
    }

}

@keyframes opacity{
    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }

}

@keyframes opacityBack{
    0% {
        opacity: 1;
    }

    95% {
        opacity: 0;
        
    }
    
    100% {
        display: none;
    }
}


